/* * $Id$ * * Warning: This file must be in MacOS format (CR only) or Rez won't be * able to read it. */#include "Types.r"#include "resource.h"/* These define's are used in the MENU resources to disable specific * menu items. */#define AllItems	0b1111111111111111111111111111111	/* 31 flags */#define MenuItem1	0b00001#define MenuItem2	0b00010#define MenuItem3	0b00100#define MenuItem4	0b01000#define MenuItem5	0b10000resource 'MBAR' (rMenuBar, preload) {	{ mApple, mFile, mView };};resource 'MENU' (mApple, "Apple", preload) {	mApple,	textMenuProc,	AllItems & ~MenuItem2,	/* Disable item #2 */	enabled,	apple,	{ "About SarienЩ", noicon, nokey, nomark, plain;	  "-", noicon, nokey, nomark, plain	}};resource 'MENU' (mFile, "File", preload) {	mFile,	textMenuProc,	AllItems,	enabled,	"File",	{ "Quit", noicon, "Q", nomark, plain }};resource 'MENU' (mView, "View", preload) {	mView,	textMenuProc,	AllItems & ~MenuItem2 & ~MenuItem4,	enabled,	"View",	{ "Sarien colors", noicon, "P", nomark, plain;	  "-", noicon, nokey, nomark, plain;	  "Hi-res mode", noicon, "H", nomark, plain;	  "-", noicon, nokey, nomark, plain;	  "Double size", noicon, "S", nomark, plain;	  "Aspect ratio", noicon, "R", nomark, plain	}};resource 'DLOG' (rAbout, "About SarienЩ") {	{ 100, 100, 412, 580 },	dBoxProc,	visible,	noGoAway,	0x0,	rAbout,	"About Sarien",	centerMainScreen};resource 'dctb' (rAbout) {	{		wContentColor,	0xcccc, 0xcccc, 0xcccc,		wFrameColor,	0x0000, 0x0000, 0x0000,		wHiliteColor,	0x0000,	0x0000,	0x0000	}};resource 'DITL' (rAbout) {	 {/* 1 */ { 280, 187, 301, 304 },		button {			enabled,			"OK"		};/* 2 */ { 21, 94, 42, 420 },				/* SourceLanguage Item */		staticText {			disabled,			"Sarien - A Sierra AGI Resource Interpreter Engine"		};/* 3 */	{ 56, 94, 141, 444 },		staticText {			disabled,			"Copyright (C) 1999-2002 Stuart George. "			"Portions Copyright (C) 1998 Lance Ewing, "			"(C) 1999 Felipe Rosinha, "			"(C) 1999-2002 Claudio Matsuoka, "			"(C) 1999-2001 Igor Nesterov, "			"(C) 2001,2002 Vasyl Tsvirkunov, "			"(C) 2001,2002 Thomas Akesson."		};/* 4 */	{ 153, 94, 177, 360 },		staticText {			disabled,			"MacOS port written by Claudio Matsuoka."		};/* 5 */	{ 189, 94, 256, 440 },		staticText {			disabled,			"This program is free software; you can redistribute it and/or modify it under the terms of the GNU General Public License, version 2 or later, as published by the Free Software Foundation."		};	}};data 'pltt' (1000, preload) {	$"0022 0000 0000 0000 0000 0000 0000 0000"  /* 32 entries, plus B&W */	$"FFFF FFFF FFFF 0000 0000 0000 0000 0000"  /* white as first guy. */	$"0000 0000 0000 0002 0000 0000 0000 0000"  /* black as next guy. */	$"FFFF 0000 0000 0004 0000 0000 0000 0000"};/* here is the quintessential MultiFinder friendliness device, * the SIZE resource */resource 'SIZE' (-1) {	saveScreen,	acceptSuspendResumeEvents,	disableOptionSwitch,	canBackground,	needsActivateOnFGSwitch,	/* we do our own activate/deactivate */	backgroundAndForeground,	/* not a background-only application! */	dontGetFrontClicks,			/* not "do first click" behavior */	ignoreAppDiedEvents,		/* I'm not a debugger (sub-launching) */	is32BitCompatible,			/* run in 32-bit address space */	notHighLevelEventAware,	onlyLocalHLEvents,	notStationeryAware,	dontUseTextEditServices,	reserved,	reserved,	reserved,	8192 * 1024,				/* preferred size */	4096 * 1024					/* minimum size */};/* * Icons */resource 'FREF' (rApplFref) {	'APPL', rApplFrefLocalID, ""};type 'FAGI' as 'STR ';resource 'FAGI' (0) {			/* Creator resource ID must be 0 */	"Sarien 0.8.0 Copyright (C) 1999-2002 Stuart George and Claudio Matsuoka"};resource 'BNDL' (0) {			/* Bundle resource ID should be 0 */	'FAGI',						/* Signature */	0,							/* Creator resource ID (must be 0) */	{		'ICN#', { rApplFrefLocalID, rApplIcon },		'FREF', { rApplIconLocalID, rApplFref }